import React, { useEffect, useState } from "react";
import styled from "styled-components";
import GetData from "../Api/getData";
import ContentBorad from "./ContentBorad";

const TitleAndFilter: React.FC = () =>{
    let [data, setData] = useState<[]>([])
    let [method, setMethod] = useState<[]>([])
    let [material, setMaterial] = useState<[]>([])
    function MaterialChange(e: React.FormEvent<HTMLInputElement>){
       let value: any = e.target
       let index: number = material.findIndex(x=> x=== value)
       console.log(index)
    }
    useEffect(()=>{
    async function getData (){
      let tempData:[] = await GetData()
      setData(tempData)
      console.log(data)
    }
    getData()
    },[])

    return (
    <Container>
     <Title>들어온 요청</Title>
     <Subscription> 파트너님에게 딱 맞는 요청서를 찾아보세요</Subscription>
     <Filter>
    <FilterContainer>
    <ProcessingContainer>
    <ProcessingButton>가공방식 <img src="./arrow_drop_down_24px.png"></img></ProcessingButton>
    <Processing>
    <div><input type="checkbox" id="밀림" name="밀링" value="밀링" />
    <label htmlFor="밀림">밀림</label></div>
    <div>
    <input type="checkbox" id="선반" name="선반" value="선반"/>
    <label htmlFor="선반">선반</label>
    </div>
    </Processing> 
    </ProcessingContainer>
    <MaterialContainer>
    <MaterialButton>재료 <img src="./arrow_drop_down_24px.png"></img></MaterialButton>
    <Material onSubmit={()=>MaterialChange}>
    <div> 
    <input type="checkbox" id="알루미늄" name="알루미늄" value="알루미늄"/>
    <label htmlFor="알루미늄">알루미늄</label>
    </div>
    <div>
    <input type="checkbox" id="구리" name="구리" value="구리"/>
    <label htmlFor="구리">구리</label>
    </div>
    <div>
    <input type="checkbox" id="탄소강" name="탄소강"  value="탄소강"/>
    <label htmlFor="탄소강">탄소강</label>
    </div>
    <div>
    <input type="checkbox" id="합금강" name="합금강" value="합금강"/>
    <label htmlFor="철금강">철금강</label>
    </div>
    <div>
    <input type="checkbox" id="강철" name="강철" value="강철" />
    <label htmlFor="강철">강철</label>
    </div>
    </Material> 
    </MaterialContainer>
    <Refresh> <img src="./refresh_24px.png"></img> 필터링 리셋</Refresh>
    </FilterContainer>
    <Toggle>
    <CheckBoxWrapper>
        <CheckBox id="checkbox" type="checkbox" />
        <CheckBoxLabel htmlFor="checkbox" />
    </CheckBoxWrapper>
    <ToggleText> 상담 중인 요청만 보기</ToggleText>
    </Toggle>
     </Filter>
     <ContentBorad x={data}></ContentBorad>
    </Container>
    )

}

export default TitleAndFilter

const Container =styled.div`
width: 80%;
margin: 0 auto;
margin-top: 2rem;
`
const Title = styled.h1`
color: ${props=> props.theme.color.gray};
font-size: 20px;
font-weight: 700;
`
const Subscription = styled.div`
margin-top: 1rem;
color: ${props=> props.theme.color.gray};
font-size: 16px;
`
const Filter =styled.div`
display: flex;
justify-content: space-between;
margin-top: 2rem;
`
const FilterContainer =styled.div`
display: flex;
gap: 1rem;
`
const Refresh =styled.div`
display: flex;
justify-content: center;
align-items: center;
gap: 10px;
`
const ProcessingContainer = styled.div`
width: 6rem;
&:hover form{
    display: block;
}
`
const MaterialContainer =styled.div`
width: 5rem;

&:hover form{
    display: block;
}
`

const MaterialButton = styled.div`
border: 1px solid #939FA5;
height: 2rem;
font-size: 12px;
display: flex;
justify-content: space-evenly;
text-indent: 0.23rem;   
align-items: center;
`
const ProcessingButton =styled.div`
border: 1px solid #939FA5;
font-size: 12px;
display: flex;
justify-content: space-evenly;
text-indent: 0.23rem;
align-items: center;
height: 2rem;
`
const Processing =styled.form`
display:none;
border: 1px solid #939FA5;
position: absolute;
background: #FFFFFF;
width: 6rem;
z-index:1;
`


const Material = styled.form`
display:none;
border: 1px solid #939FA5;
width: 5rem;
position: absolute;
background: #FFFFFF;
z-index:1;
`
const Toggle = styled.div`
display: flex;
align-items: center;
justify-content: space-around;
gap: 1rem;
`
const ToggleText =styled.div`
display: flex;
align-items: center;
`
const CheckBoxWrapper = styled.div`
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
`;
const CheckBoxLabel = styled.label`

  top: 0;
  left: 0;
  width: 42px;
  border-radius: 15px;
  background: #bebebe;
  cursor: pointer;
  &::after {
    content: "";
    display: block;
    border-radius: 50%;
    width: 18px;
    height: 18px;
    margin: 3px;
    background: #ffffff;
    box-shadow: 1px 3px 3px 1px rgba(0, 0, 0, 0.2);
    transition: 0.2s;
  }
`;
const CheckBox = styled.input`
  opacity: 0;
  z-index: 1;
  border-radius: 15px;
  width: 42px;
  &:checked + ${CheckBoxLabel} {
    background: ${props=> props.theme.color.blue};
    &::after {
      content: "";
      display: block;
      border-radius: 50%;
      width: 18px;
      height: 18px;
      margin-left: 21px;
      transition: 0.2s;
    }
  }
`;
